class App extends Component {
  state = {
    countOne: 0,
    countTwo: 0,
    secondsElapsed: 0,
  }

  handlePlus1 = () => {
    console.log('clicked!');
    this.setState({
      countOne: this.state.countOne + 1,
    })
  }

  handlePlus2 = () => {
    console.log('clicked!');
    this.setState({
      countTwo: this.state.countTwo + 1,
    })
  }

  handleSubmit = () => {
    console.log('submit!');
    this.props.dispatch({ type: 'ADD_WALK', payload: this.state });

  }

  handleStartClick = () => {
    this.incrementer = setInterval(() => {
      this.setState({
        secondsElapsed: (this.state.secondsElapsed + 1)
      })
    }, 1000)
  }


  handleStopClick = () => {
    clearInterval(this.incrementer);
  }


  getSeconds = () => {
    return ('0' + this.state.secondsElapsed % 60).slice(-2);
  }

  getMinutes = () => {
    return Math.floor(this.state.secondsElapsed / 60);
  }

  render() {
    const { classes } = this.props;
    return (



      <MuiThemeProvider theme={theme}>
        <Navbar />
        <div className="App">

          {/* <Timer2 /> */}

          <Fab color="primary" aria-label="Add" onClick={this.handleStartClick} className={classes.fab}>
            Go
            </Fab>
          <Fab color="primary" size="large" aria-label="Add" onClick={this.handleStopClick} className={classes.fab}>
            Stop
            </Fab>
          <h1>{this.getMinutes()}:{this.getSeconds()}</h1>

          <Bathroom
            countOne={this.state.countOne}
            countTwo={this.state.countTwo}
            handlePlus1={this.handlePlus1}
            handlePlus2={this.handlePlus2}
          />
          <Submit submit={this.handleSubmit} />
          {/*           
          <Router>
            <div>
            <Route exact path="/" component={App} />
            <Route path="/History" component={History} />
            </div>
          </Router> */}


        </div>
      </MuiThemeProvider>





    );
  }
}



const mapReduxToProps = (reduxState) => {
  return reduxState;
};

export default withStyles(styles)(connect(mapReduxToProps)(App));
